@charset "UTF-8";

@import url('https://fonts.googleapis.com/css2?family=Open+Sans&display=swap');

* {
    margin: 0;
    padding: 0;
    outline: none;
    font-family: 'Open sans', sans-serif;
}

header {
    height: 300px;
    position: relative;
    & > section:first-child {
        display: flex;
        height: 40px;
        background-color: black;
        justify-content: space-between;
        padding: 10px;
        color: white;
        section:first-child {
            width: 200px;
            display: flex;
            align-items: center;
            button {
                height: 40px;
                border: none;
                background-color: transparent;
                color: inherit;
                font-weight: bold;
                font-size: 1.1rem;
                padding-right: 5px;
                width: 100px;
                display: flex;
                align-items: center;
                justify-content: space-around;
                svg {
                    fill: white;
                }
                &:hover {
                    color: rgb(210, 210, 210);
                    svg {
                        fill: rgb(210, 210, 210);
                    }
                }
            }
            a {
                border-left: 2px solid white;
                padding-left: 5px;
                color: inherit;
                text-decoration: none;
                &:hover {
                    text-decoration: underline;
                }
            }
        }
        section:last-child {
            display: flex;
            width: 60px;
            align-items: center;
            justify-content: space-between;
            background-color: transparent;
            button {
                background-color: transparent;
                border: none;
                &:first-child {
                    transition: width .1s,height .1s;
                    width: 24px;
                    height: 24px;
                    &:hover {
                        width: 30px;
                        height: 30px;
                    }
                }
            }
        }
    }
    & > section:last-child {
        background-color: rgba(119, 235, 255, 0.275);
        display: flex;
        justify-content: center;
        align-items: center;
        height: 240px;
        section {
            display: flex;
            flex-direction: column;
            height: 240px;
            align-items: center;
            justify-content: space-between;
            height: 120px;
            h1 {
                font-size: 2.3rem;
            }
            input {
                padding: 10px;
                border-radius: 5px;
                border: 1px solid black;
                width: 80vw;
                font-size: .8rem;
                &::placeholder {
                    font-size: .8rem;
                }
            }
        }
    }
}

main {
    padding: 10px;
    display: flex;
    flex-flow: column wrap;
    align-items: center;
    gap: 50px;
    padding-top: 70px;
    & > section {
        width: 80%;
        height: 700px;
        article {
            display: flex;
            justify-content: center;
            align-items: center;
            gap: 10px;
            height: 220px;
            section:first-child {
                width: 100px;
                height: 100%;
                display: flex;
                align-items: flex-start;
                justify-content: center;
                img {
                    width: 3.5rem;
                }
            }
            section:last-child {
                display: flex;
                flex-direction: column;
                justify-content: start;
                gap: 10px;
                align-items: flex-start;
                width: 400px;
                height: 100%;
                h2 {
                    font-weight: 700;
                }
                p:last-child {
                    a {
                        text-decoration: none;
                        &::after {
                            content: "â†’";
                            padding-left: 10px;
                        }
                        &:hover, &:hover * {
                            text-decoration: underline;
                        }
                    }
                }
            }
        }
    }
}

footer {
    padding: 20px 10px 10px 20px;
    background-color: black;
    & * {
        color: white;
        text-decoration: none;
    }
    & a:hover {
        text-decoration: underline;
    }
    article {
        article {
            display: flex;
            flex-flow: row wrap;
            width: 99%;
            & > section {
                width: 50%;
                height: 200px;
                h2 {
                    margin-bottom: 10px;
                }
                ul {
                    list-style: none;
                }
                section {
                    h3 {
                        margin-top: 20px;
                    }
                }
            }
        }
    }
    & > section {
        width: 90vw;
        text-align: left;
        height: 120px;
        margin-right: 45vw;
        display: flex;
        flex-direction: column;
        justify-content: center;
        padding-bottom: 20px;
        section:last-child {
            pre, pre p {
                white-space: pre-line;
            }
        }
    }
}
