@charset "UTF-8";

@import url('https://fonts.googleapis.com/css2?family=Open+Sans&display=swap');

$topheight: 40px;
$toppadding: 10px;
$inputpadding: 10px;

* {
    margin: 0;
    padding: 0;
    outline: none;
    font-family: 'Open sans', sans-serif;
}

header {
    height: 300px;
    position: relative;
    & > section:first-child {
        display: flex;
        height: $topheight;
        background-color: black;
        justify-content: space-between;
        padding: $toppadding;
        color: white;
        section:first-child {
            width: 200px;
            display: flex;
            align-items: center;
            button {
                height: 40px;
                border: none;
                cursor: pointer;
                background-color: transparent;
                color: inherit;
                font-weight: bold;
                font-size: 1.1rem;
                padding-right: 5px;
                width: 100px;
                display: flex;
                align-items: center;
                justify-content: space-around;
                svg {
                    fill: white;
                }
                &:hover {
                    color: rgb(210, 210, 210);
                    svg {
                        fill: rgb(210, 210, 210);
                    }
                }
            }
            a {
                border-left: 2px solid white;
                padding-left: 5px;
                color: inherit;
                text-decoration: none;
                &:hover {
                    text-decoration: underline;
                }
            }
        }
        section:last-child {
            display: flex;
            width: 60px;
            align-items: center;
            justify-content: space-between;
            background-color: transparent;
            button {
                cursor: pointer;
                background-color: transparent;
                border: none;
                &:first-child {
                    transition: width .1s,height .1s;
                    width: 24px;
                    height: 24px;
                    aside {
                        background-color: white;
                        position: absolute;
                        z-index: 99;
                        width: 100%;
                        left: 0;
                        top: 0;
                        height: calc($topheight + ($toppadding * 2));
                        display: none;
                        flex-flow: row wrap;
                        justify-content: center;
                        align-items: center;
                        input[type=text] {
                            width: 75%;
                            height: 20px;
                            padding: $inputpadding;
                            border-radius: 5px;
                        }
                        div {
                            width: 20px;
                            cursor: pointer;
                            height: 20px;
                            color: whites;
                            background-color: transparent;
                            padding: 10px;
                            border: none;
                            position: relative;
                            display: flex;
                            flex-flow: column;
                            justify-content: center;
                            align-items: center;
                            &:hover {
                                & > span {
                                    height: 6px;
                                    width: 27px;
                                    &:first-child {
                                        transform: rotate(45deg) translateY(4.5px);
                                    }
                                    &:last-child {
                                        transform: rotate(-45deg) translateY(-4.5px);
                                    }
                                }
                            }
                            span {
                                background-color: black;
                                width: 20px;
                                color: wheat;
                                height: 5px;
                                display: inline-block;
                                border-radius: 5px;
                                transition: height linear .1s, width linear .1s;
                                &:first-child {
                                    transform: rotate(45deg) translateY(4px);
                                }
                                &:last-child {
                                    transform: rotate(-45deg) translateY(-4px);
                                }
                            }
                        }
                    }
                    &:hover {
                        width: 30px;
                        height: 30px;
                    }
                }
            }
        }
    }
    & > section:last-child {
        background-color: rgba(119, 235, 255, 0.275);
        display: flex;
        justify-content: center;
        align-items: center;
        height: 240px;
        section {
            display: flex;
            flex-direction: column;
            height: 240px;
            align-items: center;
            justify-content: space-between;
            height: 120px;
            h1 {
                font-size: 2.3rem;
            }
            &:has(input:hover) {
                & > input {
                    border-color: rgb(67, 67, 247);
                }
                & > svg {
                    fill: rgb(67, 67, 247);
                }
            }
            &:has(svg:hover) {
                & > svg {
                    fill: rgb(67, 67, 247);
                }
            }
            article {
                width: 90vw;
                height: max-content;
                display: flex;
                flex-flow: row wrap;
                justify-content: center;
                align-items: center;
                position: relative;
                input {
                    padding: $inputpadding;
                    border-radius: 5px;
                    border: 1px solid black;
                    height: 20px;
                    width: 90%;
                    font-size: .8rem;
                    box-shadow: 5px 5px 5px rgba(0, 0, 0, 0.052);
                    position: relative;
                    transition: border-color .5s;
                    &::placeholder {
                        font-size: .8rem;
                    }
                }
                svg {
                    position: absolute;
                    right: 20px;
                    fill: black;
                    transition: border-color .5s;
                }
            }
        }
    }
}

main {
    padding: 10px;
    display: flex;
    flex-flow: column wrap;
    align-items: center;
    gap: 50px;
    padding-top: 70px;
    & > section {
        width: 80%;
        height: 700px;
        article {
            display: flex;
            justify-content: center;
            align-items: center;
            gap: 10px;
            height: 220px;
            section:first-child {
                width: 100px;
                height: 100%;
                display: flex;
                align-items: flex-start;
                justify-content: center;
                img {
                    width: 3.5rem;
                }
            }
            section:last-child {
                display: flex;
                flex-direction: column;
                justify-content: start;
                gap: 10px;
                align-items: flex-start;
                width: 400px;
                height: 100%;
                h2 {
                    font-weight: 700;
                }
                p:last-child {
                    a {
                        text-decoration: none;
                        &::after {
                            content: "â†’";
                            padding-left: 10px;
                        }
                        &:hover, &:hover * {
                            text-decoration: underline;
                        }
                    }
                }
            }
        }
    }
}

footer {
    padding: 20px 10px 10px 20px;
    background-color: black;
    & * {
        color: white;
        text-decoration: none;
    }
    & a:hover {
        text-decoration: underline;
    }
    article {
        article {
            display: flex;
            flex-flow: row wrap;
            width: 99%;
            & > section {
                width: 50%;
                height: 200px;
                h2 {
                    margin-bottom: 10px;
                }
                ul {
                    list-style: none;
                }
                section {
                    h3 {
                        margin-top: 20px;
                    }
                }
            }
        }
    }
    & > section {
        width: 90vw;
        text-align: left;
        height: 120px;
        margin-right: 45vw;
        display: flex;
        flex-direction: column;
        justify-content: center;
        padding-bottom: 20px;
        section:last-child {
            pre, pre p {
                white-space: pre-line;
            }
        }
    }
}
