@charset "UTF-8";

@import url('https://fonts.googleapis.com/css2?family=Open+Sans&display=swap');

* {
    margin: 0;
    padding: 0;
    outline: none;
    font-family: 'Open sans', sans-serif;
}

header {
    height: 230px;
    & > section:first-child {
        display: flex;
        background-color: black;
        justify-content: space-between;
        padding: 10px;
        color: white;
        height: 20%;
        section:first-child {
            width: 200px;
            display: flex;
            align-items: center;
            button {
                height: 40px;
                border: none;
                background-color: transparent;
                color: inherit;
                font-weight: bold;
                font-size: 1.1rem;
                padding-right: 5px;
                width: 100px;
                display: flex;
                align-items: center;
                justify-content: space-around;
                svg {
                    fill: white;
                }
                &:hover {
                    color: rgb(210, 210, 210);
                    svg {
                        fill: rgb(210, 210, 210);
                    }
                }
            }
            a {
                border-left: 2px solid white;
                padding-left: 5px;
                color: inherit;
                text-decoration: none;
                &:hover {
                    text-decoration: underline;
                }
            }
        }
        section:last-child {
            display: flex;
            width: 60px;
            align-items: center;
            justify-content: space-between;
            background-color: transparent;
            button {
                background-color: transparent;
                border: none;
                &:first-child {
                    transition: width .1s,height .1s;
                    width: 24px;
                    height: 24px;
                    &:hover {
                        width: 30px;
                        height: 30px;
                        fill: red;
                    }
                }
            }
        }
    }
    & > section:last-child {
        background-color: rgba(119, 235, 255, 0.275);
        height: 100%;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        h1 {
            font-size: 2.3rem;
            margin: 20px 0px;
        }
        input {
            padding: 10px;
            border-radius: 5px;
            border: 1px solid black;
            width: 80%;
        }
    }
}

